import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Singlehack({ id, hack, username, likes, dislikes, reports }) {
    const [likeCount, setLikeCount] = useState(likes);
    const [dislikeCount, setDislikeCount] = useState(dislikes);
    const [reportCount, setReportCount] = useState(reports);

    const handleLike = () => {
        setLikeCount(likeCount + 1);
    };

    const handleDislike = () => {
        setDislikeCount(dislikeCount + 1);
    };

    const handleReport = () => {
        setReportCount(reportCount + 1);
    };

    useEffect(() => {
        async function fetchData() {
            try {
                const response = await axios.get('/api/hot');
                const cheatData = response.data.Hot.find(cheat => cheat.id === id);
                if (cheatData) {
                    setLikeCount(cheatData.likes);
                    setDislikeCount(cheatData.dislikes);
                    setReportCount(cheatData.reports);
                }
            } catch (error) {
                console.error('Error fetching cheat data:', error);
            }
        }
        fetchData();
    }, [id]);

    return (
        <div>
            <h1>{hack}</h1>
            <p>Username: {username}</p>
            <p>Likes: {likeCount}</p>
            <button onClick={handleLike}>Like</button>
            <p>Dislikes: {dislikeCount}</p>
            <button onClick={handleDislike}>Dislike</button>
            <p>Reports: {reportCount}</p>
            <button onClick={handleReport}>Report</button>
        </div>
    );
}

export default Singlehack;
