import React from 'react';

function Regulations() {
  return (
    <div className="regulations-container">
      <h2>Community Guidelines</h2>
      <p>
        Welcome to our platform! We want this space to be safe and welcoming for everyone. To ensure a positive experience for all users, we have established the following community guidelines:
      </p>
      <ul>
        <li>No sexual content: Posts containing explicit sexual content or nudity will be removed.</li>
        <li>No hate speech: Any form of hate speech, discrimination, or harassment will not be tolerated.</li>
        <li>No unethical or harmful content: Content promoting violence, self-harm, or illegal activities will be removed.</li>
        <li>Respect others: Treat fellow users with kindness, respect, and empathy.</li>
        <li>No spam or irrelevant content: Please keep your posts relevant to the platform's theme and avoid spamming.</li>
      </ul>
      <p>
        Violation of these guidelines may result in the removal of content  Thank you for helping us maintain a positive and inclusive community!
        hacks with more than 100 reports will be outomatically deleted
      </p>
    </div>
  );
}

export default Regulations;
